mapper.update:
update member
set password=#{password}
where mno=#{no}

update member
set nickname=#{nickName}
where mno=#{no}

update orders
set ostatus='구매확정'
where ono=#{ono}

update foodmanagement
set fmstatus='N'
where fmno=#{fmno}

mapper.select:
select pname as foodname, calorie
from product
where pno=#{pno}

mapper.update:
update member
set msno=1, infoupdatedate=sysdate
where mno=#{no}
